function getBlogTags() {
    titles = window.roamAlphaAPI.q(`[
		:find ?title
		:where
			[?node :node/title ?title]
			[?e :node/title]
		]`)
	blogTags = []

    for(var i in titles) {
        if(titles[i][0].indexOf("블로그") == 0) {
            blogTags.push(titles[i][0]);
        }
    }
    return blogTags
}

function getPages(tag) { // need to collect pages which has tags at the end only.
    return window.roamAlphaAPI.q(`[
  :find ?text ?child_uid
  :in $ ?title
  :where
  [?page :block/uid ?uid]
  [?page :node/title ?title]
  [?e :block/refs ?page]
  [?e :block/string ?text]
  [?e :block/uid ?child_uid]
]`, tag);
}

